                if (form.ShowDialog(this) == DialogResult.OK)
                {
                    string voice = form.InputText;
                    if (!string.IsNullOrEmpty(voice))
                    {
                        var tb = GetFocusedTextBox();

                        if (tb.SelectionLength > 0)
                        {
                            string s = tb.SelectedText;
