                            if (tb.SelectedText == tb.Text)
                            {
                                s = string.Format("<v {0}>{1}", voice, s);
                            }
                            else
                            {
                                s = string.Format("<v {0}>{1}</v>", voice, s);
                            }

                            tb.SelectedText = s;
                        }
                    }
                }
            }
        }

