            SaveSubtitleListviewIndices();
            SubtitleListview1.Fill(_subtitle, _subtitleAlternate);
            RestoreSubtitleListviewIndices();
            SubtitleListview1.SelectIndexAndEnsureVisible(_subtitleListViewIndex, true);

