            {
                Configuration.Settings.General.Undocked = false;
                UndockVideoControlsToolStripMenuItemClick(null, null);
                if (Configuration.Settings.General.UndockedVideoFullscreen)
                {
                    GoFullscreen(true);
                }
            }

            Main_Resize(null, null);

