            UiUtil.FixFonts(comboBoxSubtitleFormats);
            UiUtil.FixFonts(comboBoxEncoding);
            UiUtil.FixFonts(toolStripSplitButtonPlayRate);
            _lastTextKeyDownTicks = DateTime.UtcNow.Ticks;
