                            }
                        }
                        _subtitle.RemoveParagraphsByIndices(indicesToDelete);
                        _subtitle.Renumber();

                        SubtitleListview1.Fill(_subtitle, _subtitleOriginal);
                        if (indicesToDelete.Count > 0)
                        {
                            SubtitleListview1.SelectIndexAndEnsureVisible(FirstSelectedIndex);
