                    {
                        var i = currentParagraph.Text.IndexOf('}');
                        pre = currentParagraph.Text.Substring(0, i + 1);
                        currentParagraph.Text = currentParagraph.Text.Remove(0, i + 1);
                    }

                    currentParagraph.Text = pre + currentParagraph.Text + "</font>";
