                        }

                        color = form.Color;
                    }
                }
                else
                {
                    using (var form = new ColorChooser { Color = GetColorFromFirstLine(Configuration.Settings.General.LastColorPickerColor), ShowAlpha = false })
                    {
                        if (form.ShowDialog(this) != DialogResult.OK)
                        {
