                e.SuppressKeyPress = true;
            }
            else if (_shortcuts.MainListViewToggleQuotes == e.KeyData && InListView)
            {
                if (textBoxListViewText.Focused || textBoxListViewTextOriginal.Focused)
                {
                    SurroundWithTag("\"", "\"", selectedTextOnly: true);
                }
                else
                {
                    SurroundWithTag("\"", "\"");
