                e.SuppressKeyPress = true;
            }
            else if (_shortcuts.MainListViewToggleHiTags == e.KeyData && InListView)
            {
                var tags = Configuration.Settings.General.TagsInToggleHiTags.Split(';');
                string openingTag;
                string closingTag;
                switch (tags.Length)
                {
                    case 1:
                        openingTag = tags[0];
