        private void FixLargeFonts()
        {
            using (var graphics = CreateGraphics())
            {
                var textSize = graphics.MeasureString(buttonPlayPrevious.Text, Font);
                if (textSize.Height > buttonPlayPrevious.Height - 4)
                {
                    int newButtonHeight = 23;
